@Composable
fun ComponentPhotoSelect(
    modifier: Modifier,
    authViewModel: AuthViewModel
) {
    val context = LocalContext.current

    val file = context.createImageFile()

    val uri = FileProvider.getUriForFile(
        Objects.requireNonNull(context),
        BuildConfig.APPLICATION_ID + ".provider", file
    )

    var capturedImageUri by remember {
        mutableStateOf<Uri>(Uri.EMPTY)
    }

    val cameraLauncher =
        rememberLauncherForActivityResult(ActivityResultContracts.TakePicture()) {
            capturedImageUri = uri
        }

    val permissionLauncher = rememberLauncherForActivityResult(
        ActivityResultContracts.RequestPermission()
    ) {
        if (it) {
            Toast.makeText(context, "Permission Granted", Toast.LENGTH_SHORT).show()
            cameraLauncher.launch(uri)
        } else {
            Toast.makeText(context, "Permission Denied", Toast.LENGTH_SHORT).show()
        }
    }

    var selectImage by remember {
        mutableStateOf<Uri>(Uri.EMPTY)
    }

    val galleryLauncher = rememberLauncherForActivityResult(
        ActivityResultContracts.GetContent()
    ) {
        selectImage = it ?: Uri.EMPTY
    }

    var currentSelected by remember {
        mutableStateOf(0)
    }

    val iconOthers = mutableListOf(
        resApp.drawable.ic_camera,
        resApp.drawable.ic_gallery,
    )

    val photoDefaults = mutableListOf(
        // boy
        resApp.drawable.a1,
        resApp.drawable.a2,
        resApp.drawable.a3,
        resApp.drawable.a4,
        resApp.drawable.a5,
        resApp.drawable.a6,

        //girl
        resApp.drawable.e1,
        resApp.drawable.e2,
        resApp.drawable.e3,
        resApp.drawable.e4,
        resApp.drawable.e5,
        resApp.drawable.e6,
    )

    val view = LocalView.current

    val localDensity = LocalDensity.current

    var heightItem by remember {
        mutableStateOf(0.dp)
    }

    var uriFinal by remember {
        mutableStateOf(Uri.EMPTY)
    }

    LaunchedEffect(currentSelected) {
        if (photoDefaults.contains(currentSelected)) {
            uriFinal = context.resourceUri(currentSelected)
        }
        when (currentSelected) {
            resApp.drawable.ic_camera -> {

            }
            resApp.drawable.ic_gallery -> {

            }
        }
    }

    val heightDivider = GetDimension.dimensionOf(res = resDimension.dimen._1sdp)

    Box(modifier = modifier) {
        AndroidView(
            modifier = Modifier.fillMaxSize(),
            factory = { context ->
                ImageView(context).apply {
                    Blurry.with(context)
                        .capture(view)
                        .into(this)
                    setColorFilter(Color.parseColor("#7F000000"))
                }
            }
        )

        ConstraintLayout(
            modifier = Modifier
                .padding(horizontal = GetDimension.dimensionOf(res = resDimension.dimen._20sdp))
                .fillMaxWidth()
                .align(Alignment.Center)
                .background(itemColor)
        ) {
            val (title, divider1, divider2, listBoys, otherOptions) = createRefs()

            Text(
                modifier = Modifier
                    .constrainAs(title) {
                        start.linkTo(parent.start)
                        end.linkTo(parent.end)
                        top.linkTo(parent.top)

                        width = Dimension.matchParent
                        height = Dimension.wrapContent
                    }
                    .padding(vertical = GetDimension.dimensionOf(res = resDimension.dimen._10sdp)),
                text = stringResource(id = resApp.string.title_select_image),
                style = TextStyle(
                    fontWeight = FontWeight.Bold,
                    fontSize = GetDimension.dimensionOfText(res = resDimension.dimen._12sdp),
                    textAlign = TextAlign.Center,
                    color = backgroundColor
                )
            )

            Divider(
                modifier = Modifier.constrainAs(divider1) {
                    start.linkTo(parent.start)
                    end.linkTo(parent.end)
                    top.linkTo(title.bottom)

                    height = Dimension.value(heightDivider)
                    width = Dimension.matchParent
                },
                color = backgroundColor,
            )

            LazyVerticalGrid(
                modifier = Modifier
                    .padding(vertical = GetDimension.dimensionOf(res = resDimension.dimen._15sdp))
                    .constrainAs(listBoys) {
                        start.linkTo(parent.start)
                        end.linkTo(parent.end)
                        top.linkTo(divider1.bottom)
                        bottom.linkTo(divider2.top)

                        width = Dimension.matchParent
                        height = Dimension.wrapContent
                    },
                columns = GridCells.Fixed(5),
            ) {
                items(photoDefaults) {
                    PhotoItem(
                        modifier = Modifier
                            .padding(
                                all = GetDimension.dimensionOf(res = resDimension.dimen._7sdp),
                            )
                            .clip(shape = CircleShape)
                            .onGloballyPositioned {
                                heightItem = with(localDensity) {
                                    it.size.width.toDp()
                                }
                            }
                            .height(heightItem)
                            .border(
                                width = GetDimension.dimensionOf(res = resDimension.dimen._3sdp),
                                color = backgroundColor,
                                shape = CircleShape
                            )
                            .clickable {
                                currentSelected = it
                            },
                        photo = it,
                        isChecked = currentSelected == it
                    )
                }

                item {
                    OtherItem(
                        modifier = Modifier
                            .padding(
                                all = GetDimension.dimensionOf(res = resDimension.dimen._7sdp),
                            )
                            .clip(shape = CircleShape)
                            .onGloballyPositioned {
                                heightItem = with(localDensity) {
                                    it.size.width.toDp()
                                }
                            }
                            .height(heightItem)
                            .border(
                                width = GetDimension.dimensionOf(res = resDimension.dimen._3sdp),
                                color = backgroundColor,
                                shape = CircleShape
                            )
                            .clickable {
                                currentSelected = iconOthers[0]
                                val permissionCheckResult =
                                    ContextCompat.checkSelfPermission(
                                        context,
                                        Manifest.permission.CAMERA
                                    )
                                if (permissionCheckResult == PackageManager.PERMISSION_GRANTED) {
                                    cameraLauncher.launch(uri)
                                } else {
                                    permissionLauncher.launch(Manifest.permission.CAMERA)
                                }
                            },
                        icon = iconOthers[0],
                        isChecked = currentSelected == iconOthers[0],
                        uriPhoto = capturedImageUri
                    )
                }

                item {
                    OtherItem(
                        modifier = Modifier
                            .padding(
                                all = GetDimension.dimensionOf(res = resDimension.dimen._7sdp),
                            )
                            .clip(shape = CircleShape)
                            .onGloballyPositioned {
                                heightItem = with(localDensity) {
                                    it.size.width.toDp()
                                }
                            }
                            .height(heightItem)
                            .border(
                                width = GetDimension.dimensionOf(res = resDimension.dimen._3sdp),
                                color = backgroundColor,
                                shape = CircleShape
                            )
                            .clickable {
                                currentSelected = iconOthers[1]
                                galleryLauncher.launch("image/*")
                            },
                        icon = iconOthers[1],
                        isChecked = currentSelected == iconOthers[1],
                        uriPhoto = selectImage
                    )
                }
            }

            Divider(
                modifier = Modifier.constrainAs(divider2) {
                    start.linkTo(parent.start)
                    end.linkTo(parent.end)
                    bottom.linkTo(otherOptions.top)

                    height = Dimension.value(heightDivider)
                    width = Dimension.matchParent
                },
                color = backgroundColor,
            )

            Row(
                modifier = Modifier
                    .constrainAs(otherOptions) {
                        start.linkTo(parent.start)
                        end.linkTo(parent.end)
                        bottom.linkTo(parent.bottom)

                        height = Dimension.wrapContent
                        width = Dimension.matchParent
                    }
                    .padding(vertical = GetDimension.dimensionOf(res = resDimension.dimen._10sdp)),
                horizontalArrangement = Arrangement.Center
            ) {
                Text(
                    modifier = Modifier
                        .clickable {
                            val profileUpdates = UserProfileChangeRequest.Builder()
                                .setPhotoUri(uriFinal)
                                .build()
                            authViewModel.updateUserFirebase(profileUpdates)
                        },
                    text = stringResource(id = resApp.string.save_and_next),
                    style = TextStyle(
                        fontWeight = FontWeight.Bold,
                        fontSize = GetDimension.dimensionOfText(res = resDimension.dimen._12sdp),
                        textAlign = TextAlign.Center,
                        color = backgroundColor
                    )
                )
            }
        }
    }
}

@Composable
fun OtherItem(
    modifier: Modifier,
    icon: Int,
    isChecked: Boolean,
    uriPhoto: Uri
) {
    val painter = rememberAsyncImagePainter(
        model = uriPhoto,
        contentScale = ContentScale.Crop,
    )

    Box(modifier = modifier) {
        if (uriPhoto.path?.isNotEmpty() == true) {
            Image(
                modifier = Modifier.fillMaxSize(),
                painter = painter,
                contentDescription = null
            )

            if (isChecked) {
                Icon(
                    modifier = Modifier.align(Alignment.Center),
                    painter = painterResource(id = resApp.drawable.ic_checked),
                    tint = itemColor,
                    contentDescription = "checked"
                )
            }
        } else {
            Icon(
                modifier = Modifier.align(Alignment.Center),
                painter = painterResource(id = icon),
                tint = splashColor,
                contentDescription = "checked"
            )
        }
    }
}

@Composable
fun PhotoItem(
    modifier: Modifier,
    photo: Int,
    isChecked: Boolean
) {

    val painter = rememberAsyncImagePainter(
        model = photo,
        contentScale = ContentScale.Crop,
    )

    Box(modifier = modifier) {
        Image(
            modifier = Modifier.fillMaxSize(),
            painter = painter,
            contentDescription = null
        )

        if (isChecked) {
            Icon(
                modifier = Modifier.align(Alignment.Center),
                painter = painterResource(id = resApp.drawable.ic_checked),
                tint = itemColor,
                contentDescription = "checked"
            )
        }
    }
}

@Composable
fun MessagesContent(
    modifier: Modifier,
    messageList: List<Message>,
    chatViewModel: MFChatViewModel,
    onClickMessage: () -> Unit
) {
    val state = rememberLazyListState()

    val index = remember { derivedStateOf { state.firstVisibleItemIndex } }

    val isResetIndex by chatViewModel.isResetIndex.collectAsState()

    LaunchedEffect(isResetIndex) {
        if (isResetIndex) {
            state.animateScrollToItem(index = 0)
            chatViewModel.resetIndex(false)
        }
    }

    if (index.value == 0) {
        chatViewModel.hideFavorite(false)
    } else {
        chatViewModel.hideFavorite(true)
    }

    LazyColumn(
        modifier = modifier,
        state = state
    ) {
        items(messageList) {
            ItemMessage(
                modifier = Modifier
                    .padding(vertical = GetDimension.dimensionOf(res = resDimension.dimen._10sdp))
                    .fillMaxWidth(),
                message = it
            )
        }
    }
}

@Composable
fun ItemMessage(
    modifier: Modifier,
    message: Message,
) {
    Row(
        modifier = modifier,
        verticalAlignment = Alignment.CenterVertically
    ) {
        AndroidView(
            modifier = Modifier
                .clip(RoundedCornerShape(50))
                .size(GetDimension.dimensionOf(res = resDimension.dimen._40sdp)),
            factory = { context ->
                ImageView(context).apply {
                    Glide.with(context)
                        .load(message.photo)
                        .error(resApp.drawable.load_fail)
                        .centerCrop()
                        .into(this)
                }
            })

        Column(
            modifier = Modifier
                .padding(horizontal = GetDimension.dimensionOf(res = resDimension.dimen._10sdp))
                .weight(1f),
            verticalArrangement = Arrangement.Center
        ) {
            Text(
                modifier = Modifier
                    .fillMaxWidth()
                    .padding(
                        bottom = GetDimension.dimensionOf(res = com.longhrk.dimension.R.dimen._5sdp),
                    ),
                text = message.name,
                maxLines = 1,
                overflow = TextOverflow.Ellipsis,
                style = TextStyle(
                    fontWeight = FontWeight.Bold,
                    textAlign = TextAlign.Start,
                    fontSize = GetDimension.dimensionOfText(res = com.longhrk.dimension.R.dimen._16sdp),
                    color = itemColor
                )
            )

            Text(
                modifier = Modifier
                    .fillMaxWidth()
                    .padding(
                        bottom = GetDimension.dimensionOf(res = com.longhrk.dimension.R.dimen._5sdp),
                    ),
                text = message.content,
                maxLines = 1,
                overflow = TextOverflow.Ellipsis,
                style = TextStyle(
                    fontWeight = FontWeight.Light,
                    textAlign = TextAlign.Start,
                    fontSize = GetDimension.dimensionOfText(res = com.longhrk.dimension.R.dimen._14sdp),
                    color = itemColor
                )
            )
        }

        Text(
            text = message.time,
            maxLines = 1,
            overflow = TextOverflow.Ellipsis,
            style = TextStyle(
                fontWeight = FontWeight.Bold,
                textAlign = TextAlign.Start,
                fontSize = GetDimension.dimensionOfText(res = com.longhrk.dimension.R.dimen._16sdp),
                color = itemColor
            )
        )
    }
}

data class Message(
    val photo: String,
    val name: String,
    var content: String,
    val time: String
)